import React, { useState } from "react";

const EventPractice = () => {
  const [message, setMessage] = useState("");

  const onChange = e => {
    setMessage(e.target.value);
  };

  return (
    <div>
      <h1>이벤트 연습</h1>
      <input
        type="text"
        name="message"
        placeholder="아무거나 입력해 보세요"
        value={message}
        onChange={e => onChange(e)}
      />

      <button
        onClick={() => {
          alert(message);
        }}
      >
        확인
      </button>
    </div>
  );
};

export default EventPractice;
