import React, { useState } from "react";
import EventPractice from "./Components/EventPractice";

const App = () => {
  const [form, setForm] = useState({
    username: "",
    message: ""
  });
  const { username, message } = form;

  const onChange = e => {
    const nextForm = {
      ...form,
      [e.target.name]: e.target.value
    };
    setForm(nextForm);
  };

  const onClick = () => {
    alert(username + ": " + message);
    setForm({
      username: "",
      message: ""
    });
  };

  const onKeyPress = e => {
    if (e.key === "Enter") {
      onClick();
    }
  };

  return (
    <div>
      <EventPractice
        onChange={onChange}
        onClick={onClick}
        onKeyPress={onKeyPress}
      ></EventPractice>
    </div>
  );
};

export default App;
